import { useEffect, useRef, useState } from "react";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Tabs, TabsList, TabsTrigger } from "@/components/ui/tabs";
import Chart from "chart.js/auto";

export default function MarketTrendChart() {
  const chartRef = useRef<HTMLCanvasElement>(null);
  const [chartInstance, setChartInstance] = useState<Chart | null>(null);
  const [selectedTimeframe, setSelectedTimeframe] = useState("1W");
  const [selectedCoin, setSelectedCoin] = useState("BTC");

  // Sample data for the chart
  const chartData = {
    BTC: {
      labels: Array(24).fill(''),
      data: [39500, 39800, 40200, 40100, 39900, 39700, 39800, 40200, 40500, 40700, 41000, 41200, 41300, 41100, 41400, 41800, 42100, 42300, 42400, 42100, 42300, 42400, 42300, 42384],
      currentPrice: "$42,384.52",
      change: "+$1,245.23",
      percentChange: "+3.2%"
    },
    ETH: {
      labels: Array(24).fill(''),
      data: [2800, 2750, 2790, 2820, 2780, 2760, 2800, 2850, 2900, 2950, 2980, 3050, 3100, 3080, 3120, 3150, 3180, 3190, 3200, 3180, 3210, 3230, 3250, 3240],
      currentPrice: "$3,240.18",
      change: "+$440.18",
      percentChange: "+15.7%"
    },
    SOL: {
      labels: Array(24).fill(''),
      data: [105, 102, 104, 106, 103, 101, 104, 108, 110, 112, 114, 116, 118, 119, 117, 120, 122, 124, 123, 125, 128, 130, 131, 132],
      currentPrice: "$132.75",
      change: "+$27.75",
      percentChange: "+26.4%"
    }
  };

  // Generate different data sets for different timeframes
  const getDataForTimeframe = (coin: string, timeframe: string) => {
    const baseData = chartData[coin as keyof typeof chartData].data;
    switch (timeframe) {
      case '1D':
        return baseData.slice(-24); // Last 24 hours
      case '1W':
        return baseData.slice(-7).map((_, i) => baseData[i * 3 + 10]); // Weekly view
      case '1M':
        return Array(30).fill(0).map((_, i) => baseData[0] + Math.random() * 2000 - 1000); // Monthly simulation
      case '1Y':
        return Array(12).fill(0).map((_, i) => baseData[0] + Math.random() * 5000 - 2500); // Yearly simulation
      default:
        return baseData;
    }
  };

  useEffect(() => {
    if (chartRef.current) {
      // Destroy existing chart if it exists
      if (chartInstance) {
        chartInstance.destroy();
      }

      const ctx = chartRef.current.getContext('2d');

      if (ctx) {
        // Create gradient fill
        const gradientFill = ctx.createLinearGradient(0, 0, 0, 300);
        gradientFill.addColorStop(0, 'rgba(0, 229, 255, 0.3)');
        gradientFill.addColorStop(1, 'rgba(0, 229, 255, 0)');

        const data = getDataForTimeframe(selectedCoin, selectedTimeframe);

        const newChartInstance = new Chart(ctx, {
          type: 'line',
          data: {
            labels: Array(data.length).fill(''),
            datasets: [{
              label: `${selectedCoin} Price`,
              data: data,
              borderColor: '#00E5FF',
              borderWidth: 2,
              pointRadius: 0,
              tension: 0.4,
              fill: true,
              backgroundColor: gradientFill
            }]
          },
          options: {
            responsive: true,
            maintainAspectRatio: false,
            animation: {
              duration: 1000,
              easing: 'easeInOutQuart'
            },
            plugins: {
              legend: {
                display: false
              },
              tooltip: {
                mode: 'index',
                intersect: false,
                backgroundColor: 'rgba(13, 14, 25, 0.9)',
                titleColor: '#00E5FF',
                bodyColor: '#fff',
                borderColor: '#00E5FF',
                borderWidth: 1
              }
            },
            scales: {
              x: {
                grid: {
                  display: false
                },
                ticks: {
                  display: false
                }
              },
              y: {
                position: 'right',
                grid: {
                  color: 'rgba(255, 255, 255, 0.05)'
                },
                ticks: {
                  color: 'rgba(255, 255, 255, 0.5)',
                  padding: 10,
                  callback: function(value) {
                    return '$' + value.toLocaleString();
                  }
                }
              }
            }
          }
        });

        setChartInstance(newChartInstance);
      }
    }
  }, [selectedCoin, selectedTimeframe]);

  return (
    <Card className="bg-[#191A2A] border-white/10 animate-[fadeInUp_0.8s_ease-out_0.3s] animate-fill-both">
      <CardHeader className="border-b border-white/5 p-4 flex flex-row items-center justify-between">
        <h3 className="font-orbitron text-lg">Market Trend Analysis</h3>
        <div className="flex items-center space-x-2">
          <Button 
            variant={selectedCoin === "BTC" ? "secondary" : "ghost"} 
            size="sm" 
            className={selectedCoin === "BTC" ? "bg-cyan-400/20 text-cyan-400 hover:bg-cyan-400/30" : "bg-white/5 text-gray-400 hover:bg-white/10"}
