              label="Dashboard" 
              isActive={location === "/"} 
            />
            <SidebarItem 
              href="/whale-tracker" 
              icon="ri-funds-line" 
              label="Whale Tracker" 
              isActive={location === "/whale-tracker"} 
            />
            <SidebarItem 
              href="/ai-trends" 
              icon="ri-brain-line" 
              label="AI Trends" 
              isActive={location === "/ai-trends"} 
            />
            <SidebarItem 
              href="/ai-assistant" 
              icon="ri-robot-line" 
              label="AI Assistant" 
              isActive={location === "/ai-assistant"} 
            />
            <SidebarItem 
              href="/wallet-insights" 
              icon="ri-wallet-3-line" 
