                )}
                {prediction.accuracy && (
                  <div>
                    <span className="text-gray-400">Accuracy: </span>
                    <span className="text-purple-400">
                      <AnimatedCounter end={prediction.accuracy} suffix="%" />
                    </span>
                  </div>
                )}
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </motion.div>
    </motion.div>
  );
});

